import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useState, useEffect } from 'react';
import Login from '../Components/Login';


export default function DefaultAppBar() {
  let theme = createTheme({  });
  theme = createTheme(theme, {
    palette: {
      salmon: theme.palette.augmentColor({
        color: {
          main: '#FF5733',
        },
        name: 'salmon',
      }),
    },
  });

  const [isLoginModalOpen, setLoginModalOpen] = useState(false);

  const openLoginModal = () => {
    setLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setLoginModalOpen(false);
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('https://api-petroforecast-ec6416a1a32f.herokuapp.com/users/login',
        { username, password });
      console.log(response.data);
      if (response.data) {
        setIsLoggedIn(true);
        setCurrentUser(response.data);
        localStorage.setItem("currentUser", JSON.stringify(response.data));
        //console.log((JSON.parse((localStorage.getItem("currentUser"))).isComplete));
        let profileComplete = JSON.parse((localStorage.getItem("currentUser"))).isComplete;
        if (profileComplete === 'false') {
          setProfileComplete(false);
        } else {
          setProfileComplete(true);
        }
        localStorage.setItem("currentPage", "");
        if (!isProfileComplete) {
          navigate("/profile-completion");
        } else {
          navigate("/dashboard");
        }
      }
    } catch (error) {
      setRegistrationAlert({
        open: true,
        severity: 'error',
        message: 'Failed to login. Username or password incorrect!',
      });

      console.error('Login failed:', error);

      // alert("Failed to login")
      // console.error('Login failed:', error);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar color='salmon'>
          <Toolbar>
          <Button
          href="/"
          sx={{
            '&:hover': {
              backgroundColor: 'inherit',
            },
            'color': "white",
          }}>
            <Typography variant="h6" component="div" sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}>
             Houston Museum of Fine Arts
            </Typography></Button>

            <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            </Box>

            <Box sx={{
              flexGrow: 0,
            }}>
              <Button href="/admission" color="inherit">Admission</Button>
              <Button color="inherit">Link 2</Button>
              <Button color="inherit">Link 3</Button>
              <Button color="inherit">Link 4</Button>
              <Button
                variant="outlined"
                color="inherit"
                onClick={openLoginModal} // TODO: add login logic
                sx={{
                  '&:hover': {
                    backgroundColor: 'rgba(0, 191, 255, 0.3)',
                  },
                }}
              >
                Employee Login
              </Button>

            </Box>
          </Toolbar>
        </AppBar>
      </Box>
    </ThemeProvider>
  );
}
